import "./form-input.styles.scss";

const FormInput = ({ handleChange, label, ...otherProps }) => {
  return (
    <div className="group">
      <input
        type="text"
        className="form-input"
        onChange={handleChange}
        {...otherProps}
      />
      {label ? (
        <label className={`${otherProps.value.length} ? 'shrink' : '' `}>
          {label}
        </label>
      ) : null}
    </div>
  );
};
export default FormInput;
